<script src="https://www.lucaspiano.com/scripts/swfobject.js" type="text/javascript"></script><script src="https://ajax.googleapis.com/ajax/libs/jquery/1.11.1/jquery.min.js" type="text/javascript"></script><!--Plugins--><link rel="stylesheet" href="https://www.lucaspiano.com/yt-master/ytv.css" type="text/css" media="all" /><link rel="stylesheet" href="https://www.lucaspiano.com/venobox/venobox.css" type="text/css" media="all" /><script src="https://www.lucaspiano.com/yt-master/ytv.js" type="text/javascript"></script><script src="https://www.lucaspiano.com/venobox/venobox.min.js" type="text/javascript"></script><script type="text/javascript">$(document).mouseup(function (e){	var container = $("#languageMenu");	if (!container.is(e.target)		&& container.has(e.target).length === 0)	{		container.hide();	}});	$(document).ready(function(){	$('.venobox').venobox({        framewidth: '800px',        // default: ''        frameheight: '900px',       // default: ''        border: '10px',             // default: '0'        bgcolor: '#999',         // default: '#fff'        titleattr: 'data-title',    // default: 'title'        numeratio: true,            // default: false        infinigall: true            // default: false    });    $('.venobox-playlist').venobox({        framewidth: '1020px',        // default: ''        frameheight: '900px',       // default: ''        border: '10px',             // default: '0'        bgcolor: '#999',         // default: '#fff'        titleattr: 'data-title',    // default: 'title'        numeratio: true,            // default: false        infinigall: true            // default: false    });	$("#lnkChangeLang").click(function(){		$("#languageMenu").show();	});			$("ul.subnav").parent().append("<span></span>"); //Only shows drop down trigger when js is enabled - Adds empty span tag after ul.subnav	$("ul.topnav li span").click(function() { //When trigger is clicked...		//Following events are applied to the subnav itself (moving subnav up and down)		$(this).parent().find("ul.subnav").slideDown('fast').show(); //Drop down the subnav on click		$(this).parent().hover(function() {		}, function(){			$(this).parent().find("ul.subnav").slideUp('slow'); //When the mouse hovers out of the subnav, move it back up		});		//Following events are applied to the trigger (Hover events for the trigger)		}).hover(function() {			$(this).addClass("subhover"); //On hover over, add class "subhover"		}, function(){	//On Hover Out			$(this).removeClass("subhover"); //On hover out, remove class "subhover"	});});function linkTo(optVal){if(optVal=="") return false;window.location='https://www.lucaspiano.com/songsheets/artists.php?Codigo='+optVal;}</script><div id="fb-root"></div><script>(function(d, s, id) {  var js, fjs = d.getElementsByTagName(s)[0];  if (d.getElementById(id)) return;  js = d.createElement(s); js.id = id;  js.src = "//connect.facebook.net/en_US/all.js#xfbml=1&appId=461573680537774";  fjs.parentNode.insertBefore(js, fjs);}(document, 'script', 'facebook-jssdk'));</script><script type="text/javascript" src="https://www.lucaspiano.com/buttons.js"></script><script type="text/javascript">stLight.options({publisher: "6b23f4d5-7a38-47de-9d07-6766dee05178"}); </script>